   321→function KursDialog({
   322→  open,
   323→  onOpenChange,
   324→  record,
   325→  dozenten,
   326→  raeume,
   327→  onSuccess,
   328→}: {
   329→  open: boolean;
   330→  onOpenChange: (open: boolean) => void;
   331→  record: Kurse | null;
   332→  dozenten: Dozenten[];
   333→  raeume: Raeume[];
   334→  onSuccess: () => void;
   335→}) {
   336→  const isEditing = !!record;
   337→  const [submitting, setSubmitting] = useState(false);
   338→  const [titel, setTitel] = useState('');
   339→  const [beschreibung, setBeschreibung] = useState('');
   340→  const [startdatum, setStartdatum] = useState('');
   341→  const [enddatum, setEnddatum] = useState('');
   342→  const [maxTeilnehmer, setMaxTeilnehmer] = useState('');
   343→  const [preis, setPreis] = useState('');
   344→  const [dozentId, setDozentId] = useState('');
   345→  const [raumId, setRaumId] = useState('');
   346→
   347→  useEffect(() => {
   348→    if (open) {
   349→      if (record) {
   350→        setTitel(record.fields.titel || '');
   351→        setBeschreibung(record.fields.beschreibung || '');
   352→        setStartdatum(record.fields.startdatum?.split('T')[0] || '');
   353→        setEnddatum(record.fields.enddatum?.split('T')[0] || '');
   354→        setMaxTeilnehmer(record.fields.maximale_teilnehmer?.toString() || '');
   355→        setPreis(record.fields.preis?.toString() || '');
   356→        setDozentId(extractRecordId(record.fields.dozent) || '');
   357→        setRaumId(extractRecordId(record.fields.raum) || '');
   358→      } else {
   359→        setTitel('');
   360→        setBeschreibung('');
   361→        setStartdatum('');
   362→        setEnddatum('');
   363→        setMaxTeilnehmer('');
   364→        setPreis('');
   365→        setDozentId('');
   366→        setRaumId('');
   367→      }
   368→    }
   369→  }, [open, record]);
   370→
   371→  async function handleSubmit(e: React.FormEvent) {
   372→    e.preventDefault();
   373→    setSubmitting(true);
   374→    try {
   375→      const fields: Kurse['fields'] = {
   376→        titel: titel || undefined,
   377→        beschreibung: beschreibung || undefined,
   378→        startdatum: startdatum || undefined,
   379→        enddatum: enddatum || undefined,
   380→        maximale_teilnehmer: maxTeilnehmer ? Number(maxTeilnehmer) : undefined,
   381→        preis: preis ? Number(preis) : undefined,
   382→        dozent: dozentId ? createRecordUrl(APP_IDS.DOZENTEN, dozentId) : undefined,
   383→        raum: raumId ? createRecordUrl(APP_IDS.RAEUME, raumId) : undefined,
   384→      };
   385→      if (isEditing) {
   386→        await LivingAppsService.updateKurseEntry(record!.record_id, fields);
   387→        toast.success('Kurs wurde aktualisiert.');
   388→      } else {
   389→        await LivingAppsService.createKurseEntry(fields);
   390→        toast.success('Neuer Kurs wurde erstellt.');
   391→      }
   392→      onOpenChange(false);
   393→      onSuccess();
   394→    } catch (err) {
   395→      toast.error(`Fehler: ${err instanceof Error ? err.message : 'Unbekannter Fehler'}`);
   396→    } finally {
   397→      setSubmitting(false);
   398→    }
