   291→// Exported so Dashboard can reuse it
   292→export function AnmeldungDialog({
   293→  open,
   294→  onOpenChange,
   295→  record,
   296→  kurse,
   297→  teilnehmer,
   298→  onSuccess,
   299→}: {
   300→  open: boolean;
   301→  onOpenChange: (open: boolean) => void;
