   352→      setSubmitting(false);
   353→    }
   354→  }
   355→
   356→  return (
   357→    <Dialog open={open} onOpenChange={onOpenChange}>
   358→      <DialogContent className="sm:max-w-md">
   359→        <DialogHeader>
   360→          <DialogTitle>{isEditing ? 'Anmeldung bearbeiten' : 'Neue Anmeldung'}</DialogTitle>
   361→        </DialogHeader>
   362→        <form onSubmit={handleSubmit} className="space-y-4">
   363→          <div className="space-y-2">
   364→            <Label htmlFor="anm-kurs">Kurs</Label>
   365→            <Select value={kursId || 'none'} onValueChange={(v) => setKursId(v === 'none' ? '' : v)}>
   366→              <SelectTrigger id="anm-kurs">
   367→                <SelectValue placeholder="Kurs wählen..." />
   368→              </SelectTrigger>
   369→              <SelectContent>
   370→                <SelectItem value="none">Kein Kurs</SelectItem>
   371→                {kurse.map((k) => (
   372→                  <SelectItem key={k.record_id} value={k.record_id}>
   373→                    {k.fields.titel || 'Ohne Titel'}
   374→                  </SelectItem>
   375→                ))}
   376→              </SelectContent>
   377→            </Select>
