   307→  const isEditing = !!record;
   308→  const [submitting, setSubmitting] = useState(false);
   309→  const [kursId, setKursId] = useState('');
   310→  const [teilnehmerId, setTeilnehmerId] = useState('');
   311→  const [anmeldedatum, setAnmeldedatum] = useState('');
   312→  const [bezahlt, setBezahlt] = useState(false);
   313→
   314→  useEffect(() => {
   315→    if (open) {
   316→      if (record) {
   317→        setKursId(extractRecordId(record.fields.kurs) || '');
   318→        setTeilnehmerId(extractRecordId(record.fields.teilnehmer) || '');
   319→        setAnmeldedatum(record.fields.anmeldedatum?.split('T')[0] || '');
   320→        setBezahlt(record.fields.bezahlt || false);
   321→      } else {
   322→        setKursId('');
   323→        setTeilnehmerId('');
   324→        setAnmeldedatum(new Date().toISOString().split('T')[0]);
   325→        setBezahlt(false);
   326→      }
   327→    }
   328→  }, [open, record]);
   329→
   330→  async function handleSubmit(e: React.FormEvent) {
   331→    e.preventDefault();
   332→    setSubmitting(true);
   333→    try {
   334→      const fields: Anmeldungen['fields'] = {
   335→        kurs: kursId ? createRecordUrl(APP_IDS.KURSE, kursId) : undefined,
   336→        teilnehmer: teilnehmerId ? createRecordUrl(APP_IDS.TEILNEHMER, teilnehmerId) : undefined,
   337→        anmeldedatum: anmeldedatum || undefined,
   338→        bezahlt,
   339→      };
   340→      if (isEditing) {
   341→        await LivingAppsService.updateAnmeldungenEntry(record!.record_id, fields);
   342→        toast.success('Anmeldung wurde aktualisiert.');
