   427→            <div className="space-y-2">
   428→              <Label htmlFor="kurs-max">Max. Teilnehmer</Label>
   429→              <Input id="kurs-max" type="number" min="0" value={maxTeilnehmer} onChange={(e) => setMaxTeilnehmer(e.target.value)} />
   430→            </div>
   431→            <div className="space-y-2">
   432→              <Label htmlFor="kurs-preis">Preis (EUR)</Label>
   433→              <Input id="kurs-preis" type="number" min="0" step="0.01" value={preis} onChange={(e) => setPreis(e.target.value)} />
   434→            </div>
   435→          </div>
   436→          <div className="space-y-2">
   437→            <Label htmlFor="kurs-dozent">Dozent</Label>
   438→            <Select value={dozentId || 'none'} onValueChange={(v) => setDozentId(v === 'none' ? '' : v)}>
   439→              <SelectTrigger id="kurs-dozent">
   440→                <SelectValue placeholder="Dozent wählen..." />
   441→              </SelectTrigger>
   442→              <SelectContent>
   443→                <SelectItem value="none">Kein Dozent</SelectItem>
   444→                {dozenten.map((d) => (
   445→                  <SelectItem key={d.record_id} value={d.record_id}>
