      setMaxTN(record?.fields.maximale_teilnehmer?.toString() ?? '');
      setPreis(record?.fields.preis?.toString() ?? '');
      setDozent(extractRecordId(record?.fields.dozent) ?? '');
      setRaum(extractRecordId(record?.fields.raum) ?? '');
