     9→export function extractRecordId(url: string | null | undefined): string | null {
    10→  if (!url) return null;
    11→  // Extrahiere die letzten 24 Hex-Zeichen mit Regex
    12→  const match = url.match(/([a-f0-9]{24})$/i);
    13→  return match ? match[1] : null;
    14→}
    15→
    16→export function createRecordUrl(appId: string, recordId: string): string {
    17→  return `https://my.living-apps.de/rest/apps/${appId}/records/${recordId}`;
    18→}
    19→
    20→async function callApi(method: string, endpoint: string, data?: any) {
    21→  const response = await fetch(`${API_BASE_URL}${endpoint}`, {
    22→    method,
    23→    headers: { 'Content-Type': 'application/json' },
    24→    credentials: 'include',  // Nutze Session Cookies für Auth
    25→    body: data ? JSON.stringify(data) : undefined
    26→  });
    27→  if (!response.ok) throw new Error(await response.text());
    28→  // DELETE returns often empty body or simple status
    29→  if (method === 'DELETE') return true;
    30→  return response.json();
    31→}
